      }
      &[role="menuitemradio"][aria-checked="true"] .ytp-menuitem-label {
        @svg: escape(
          '<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%" viewBox="0 0 24 24" version="1.1"><path d="M9 16.2 4.8 12l-1.4 1.4L9 19 21 7l-1.4-1.4z" fill="@{text}"/></svg>'
        );
        background-image: url("data:image/svg+xml,@{svg}");
      }
    }

    /* Video player */

    .ytp-cards-button {
      .ytp-cards-button-icon {
        use {
          fill: @black;
        }
        path {
          fill: @white;
        }
      }
    }
    .ytp-cards-teaser {
      .ytp-cards-teaser-box {
        background-color: @surface2;
        border-bottom-color: @surface2;
      }
      .ytp-cards-teaser-text {
        color: @text;
      }
    }

    .html5-video-player {
      color: @white;

