@-moz-document url-prefix("http://warriorcatsarpg.com"), url-prefix("https://warriorcatsarpg.com"), url-prefix("warriorcatsarpg.com") {
body {
	--post-text-color: #cdd6f4;
	--accent-color: #b4befe;
	--accent-color-2: #b4befe;
	--mg-color: #313244;
	--claims-color: #11111b;
	--light-text-color: #1e1e2e;
	--light-bg-color: #181825;
	--forum-title-color: #b4befe;
	--board-title-color: #b4befe;
	--board-info-color: #bac2de;
	--board-info-user-color: #b4befe;
	--forum-outline-color: #313244;
	--maintitle-color: #212121;
	--forum-boxshadow: #11111b;
	--header-tab: #45475a;
	--board-div-lighter-color: #181825;
	--board-div-lighter-color-no: #313244;
	--maintitle-color2: #585b70;
	--maintitle-text-color: #bac2de;
	--maintitle-misc-color: #a6adc8;
	--header: #313244;
	--header-text: #bac2de;
	--container: #1b1919;
	--container-text: #a5adce;
	--container-bold: #b4befe;
	--summary-header: #b4befe;
	--summary-header-text: #cdd6f4;
	--misc: #181825;
	background-color: #1e1e2e;
}
#header-image {
	background-image: url("https://i.imgur.com/tc2MfAv.png") !important;
	background-position: center;
	background-size: 105%;
	background-color: #1e1e2e;
}
#background-wrapper {
	background-image: none !important;
}
#alerts-indicator {
	color: var(----accent-color-2) !important;
}
.category-forums {
	background: none;
}
div.tr-box {
	color: var(--post-text-color);
}
.mini-block {
	background: var(--accent-color-2);
}

#header-block hr {
    background-color: var(--light-text-color);
}

#searchbar .search-input {
    color: var(--light-text-color);
}

.headeric, .headerooc {
    scrollbar-color: var(--accent-color) var(--light-text-color);
}

.headeric b, .headerooc b {
    background-color: var(--accent-color);
    color: var(--light-text-color);
}

.headeric-2 .missions, .headeric-2 .gathering {
    color: var(--light-text-color);
}

.macro-large {
    color: var(--light-text-color);
}

div.code-scroll {
	color: var(--post-text-color)
}

.pagination a:link, .pagination a:visited, .pagination a:active {
    background-color: var(--accent-color-2);
    color: var(--light-text-color);
}

.stats-online-today-list {
	background: var(--forum-boxshadow);
}
.stats-online-list {
	background: #181825;
}
#top-bar,
#navstrip {
	background-color: #181825;
}

#top-bar a:link {
	color: #b4befe;
}
#top-bar a:visited {
	color: #b4befe;
}
#credits .maintitle,
.tableborder,
.copyright,
.credits-description {
	color: #a6adc8;
}
#credits .maintitle {
	background-color: var(--claims-color);
}
#credits a {
	color: #b4befe;
}
.post-mini-profile {
	background: #181825;
	border-color: #11111b;
}
.post-no-gos {
	background: #313244;
	color: var(--post-text-color);
	border-color: #11111b;
}
.maintitle,
.profile-maintitle,
.profile-custom-content {
	background-color: #11111b;
	border-color: #313244;
}
.textinput,
.light_code,
.light-code {
	background-color: #0003;
	color: #9399b2;
}
.light_code,
.light-code {
	border: none;
