@-moz-document domain("duckduckgo.com") {
  :root:not(.dark-bg, .no-theme) {
    #catppuccin(@lightFlavor);
  }

  :root.dark-bg {
    #catppuccin(@darkFlavor);
  }

  :root.no-theme {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
