    }

    // red transparent bg (appears with "unfollow" hover)
    .r-qqmkd0 {
      background-color: fade(@red, 10%);
    }

    // red bg on hover
    .r-12d83nn {
      background-color: darken(@red, 10%);
    }

    // red bg when active
    .r-oybae9 {
      background-color: darken(@red, 15%);
    }

    .r-11mg6pl {
      border-color: if(@flavor = latte, #fff, @crust); // white border
    }

    // mask over layer
    .r-11z020y {
      background-color: fade(desaturate(darken(@accent, 10%), 50%), 12%);
    }

    // likes
    [fill="rgb(249,22,127)"],
    [fill="rgb(222,45,108)"],
