/* ==UserStyle==
@name Elk Catppuccin
@namespace github.com/catppuccin/userstyles/styles/elk
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/elk
@version 2024.12.31
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/elk/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Aelk
@description Soothing pastel theme for Elk
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("elk.zone") {
  :root.dark {
    #catppuccin(@darkFlavor);
  }
  :root.light {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    --c-primary: @accent !important;
    --c-primary-active: lighten(@accent, 10%) !important;
    --c-primary-light: @surface0 !important;
    --c-border: @surface0 !important;
    --c-border-dark: @surface1 !important;
    --c-bg-base: @base !important;
    --rgb-bg-base: #rgbify(@mantle)[] !important;
    --c-bg-active: @surface0 !important;
    --c-bg-code: @surface0 !important;
    --c-bg-selection: @accent !important;
    --c-bg-fade: @surface0 !important;
    --c-text-base: @text !important;
    --c-text-code: @rosewater !important;
    --c-text-secondary: @subtext0 !important;
    --c-text-secondary-light: @subtext1 !important;
    --c-bg-btn-disabled: @surface1 !important;
    --c-text-btn-disabled: @text !important;
    --c-text-btn: @text !important;
    --c-success: @green !important;
    --c-warning: @yellow !important;
    --c-error: @red !important;
    --c-danger: @red !important;
    --c-danger-active: lighten(@red, 10%) !important;
    --c-bg-dm: @surface0 !important;

    .btn-solid,
    [btn-solid=""] {
      color: var(--c-bg-base);
    }

    ::-webkit-scrollbar-thumb {
      background: fade(@surface1, 70%);
    }

    ::-webkit-scrollbar-thumb:hover {
      background: fade(@surface1, 80%);
    }

    [text-red],
    [text-red-600],
    [hover^="text-red"]:hover {
      color: rgba(#rgbify(@red)[], var(--un-text-opacity));
    }

    [text-blue],
    [hover^="text-blue"]:hover {
      color: rgba(#rgbify(@blue)[], var(--un-text-opacity));
    }

    [text-green],
    [hover^="text-green"]:hover {
      color: rgba(#rgbify(@green)[], var(--un-text-opacity));
    }

    [text-yellow],
    [hover^="text-yellow"]:hover {
      color: rgba(#rgbify(@yellow)[], var(--un-text-opacity));
    }

    [text-purple],
    [hover^="text-purple"]:hover {
      color: rgba(#rgbify(@lavender)[], var(--un-text-opacity));
    }

    [group-hover^="bg-purple/10"]:hover {
      background-color: fade(@lavender, 10%);
    }

    .filter-saturate-0,
    [filter-saturate-0=""] {
      filter: none;
    }
  }
}

#rgbify(@color) {
  @rgb: red(@color), green(@color), blue(@color);
}

/* deno-fmt-ignore */
