@-moz-document domain("rentry.org"), domain("rentry.co") {
  @import url("https://python.catppuccin.com/pygments/catppuccin-variables.important.css");

  .dark-mode {
    #catppuccin(@darkFlavor);
    #darkModeBtn::before {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="@{overlay2}" class="bi bi-moon-fill" viewBox="0 0 16 16"><path d="M6 .278a.77.77 0 0 1 .08.858 7.2 7.2 0 0 0-.878 3.46c0 4.021 3.278 7.277 7.318 7.277q.792-.001 1.533-.16a.79.79 0 0 1 .81.316.73.73 0 0 1-.031.893A8.35 8.35 0 0 1 8.344 16C3.734 16 0 12.286 0 7.71 0 4.266 2.114 1.312 5.124.06A.75.75 0 0 1 6 .278"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }
  }
  :root:not(.dark-mode) {
    #catppuccin(@lightFlavor);
    #darkModeBtn::before {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="@{overlay2}" class="bi bi-brightness-high-fill" viewBox="0 0 16 16"><path d="M12 8a4 4 0 1 1-8 0 4 4 0 0 1 8 0M8 0a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 0m0 13a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 13m8-5a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2a.5.5 0 0 1 .5.5M3 8a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2A.5.5 0 0 1 3 8m10.657-5.657a.5.5 0 0 1 0 .707l-1.414 1.415a.5.5 0 1 1-.707-.708l1.414-1.414a.5.5 0 0 1 .707 0m-9.193 9.193a.5.5 0 0 1 0 .707L3.05 13.657a.5.5 0 0 1-.707-.707l1.414-1.414a.5.5 0 0 1 .707 0m9.193 2.121a.5.5 0 0 1-.707 0l-1.414-1.414a.5.5 0 0 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .707M4.464 4.465a.5.5 0 0 1-.707 0L2.343 3.05a.5.5 0 1 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .708"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    --ctp-rosewater: @rosewater;
    --ctp-flamingo: @flamingo;
    --ctp-pink: @pink;
    --ctp-mauve: @mauve;
    --ctp-red: @red;
    --ctp-maroon: @maroon;
    --ctp-peach: @peach;
    --ctp-yellow: @yellow;
    --ctp-green: @green;
    --ctp-teal: @teal;
    --ctp-sky: @sky;
    --ctp-sapphire: @sapphire;
    --ctp-blue: @blue;
    --ctp-lavender: @lavender;
    --ctp-text: @text;
    --ctp-subtext1: @subtext1;
    --ctp-subtext0: @subtext0;
    --ctp-overlay2: @overlay2;
    --ctp-overlay1: @overlay1;
    --ctp-overlay0: @overlay0;
    --ctp-surface2: @surface2;
    --ctp-surface1: @surface1;
    --ctp-surface0: @surface0;
    --ctp-base: @base;
    --ctp-mantle: @mantle;
    --ctp-crust: @crust;

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    body {
      background: @crust;
      color: @text;
    }
    /* Editor and preview */
    .cm-s-default,
    .markdownx-preview,
    .entry-text,
    .tab-content {
      background: @base !important;
      color: @text !important;
    }

    /* Tab navigation */
    .nav-link {
      background: @mantle;
      color: @overlay0 !important;

      &.active {
        background: @base !important;
        color: @text !important;
      }
    }
    .form-control,
    textarea {
      background: @base !important;
      color: @text !important;
    }
    .input-success {
      box-shadow: inset 4px 0 @green !important;
    }
    .input-error {
      &,
      + .CodeMirror-wrap {
        box-shadow: @red 4px 0 inset !important;
      }
    }
    .input-warning {
      box-shadow: @yellow 4px 0 inset !important;
    }
    .btn {
      background: @base !important;
      color: @text !important;
      &:hover {
        background: @surface0 !important;
        color: @text !important;
      }
    }
    .btn-success {
      &,
      &.btn,
      &:hover {
        color: @green !important;
      }
      &#submitButton {
        background: @base !important;
        &:hover {
          background: @surface0 !important;
        }
      }
    }
    .btn-danger {
      &,
      &.btn,
      &:hover {
        color: @red !important;
      }
    }
    .clipboard {
      color: @text;
      background: none;
    }
    #progressBar {
      background-color: @blue !important;
    }
    .full-contrast:not(.active) path {
      fill: @overlay2 !important;
    }
    .full-contrast.active path {
      fill: @peach !important;
    }
    .edit-code {
      background: @mantle;
      color: @text !important;
    }
    .cb-tooltiptext {
      color: @text !important;
      background: @surface0 !important;
    }
    .cb-tooltiptext-bottom::after {
      border-color: transparent transparent @surface0;
    }
    .headerlink {
      color: @overlay1 !important;
    }
    #submitButton {
      background: @mantle !important;
    }
    // claim-guide page
    .color-change {
      &[style="color:grey"] {
        color: @overlay2 !important;
      }
      &[style="color:#039205"] {
        color: @green !important;
      }
    }
    // delete modal
    .modal-content {
      background: @crust;
      color: @text;
    }
    .modal-body {
      color: @overlay2;
    }
    .modal-header {
      color: @text !important;
    }
    .modal-header,
    .modal-footer {
      border-color: @surface1 !important;
    }
    #deleteButton {
      color: @red !important;
    }
    // footer
    a {
      color: @blue;

      &:hover {
        color: @sky !important;
      }
    }
    .text-muted {
      color: @overlay2 !important;
    }
    .text-primary {
      color: @blue !important;
    }
    .text-success {
      color: @green !important;
    }
    .text-warning,
    .text-warning-darker {
      color: @yellow !important;
    }
    .text-danger {
      color: @red !important;
    }
    // editor
    .CodeMirror-line::selection,
    .CodeMirror-line > span::selection,
    .CodeMirror-line > span > span::selection {
      background-color: fade(@overlay2, 25%);
    }
    .cm-s-default {
      .cm-formatting-admonition {
        color: @yellow;
      }
      .cm-mark {
        color: @yellow;
      }
      .cm-link {
        color: @lavender;
      }
      .cm-url {
        color: @blue;
      }
      .cm-formatting-toc {
        color: @mauve;
      }
      .cm-variable-2 {
        color: @green;
      }
      .cm-keyword {
        color: @pink;
      }
      .cm-meta {
        color: @subtext0;
      }
      .cm-comment {
        color: @pink;
      }
      .cm-hr {
        color: @surface2;
      }
      .cm-tag {
        color: @blue;
      }
      .cm-header {
        color: @text;
      }
      .cm-quote {
        color: @green;
        border-color: @green;
      }
    }
    blockquote {
      color: @green;
      border-color: @green;
    }
    mark {
      background: fade(@yellow, 25%);
      color: @text;
    }
    .spoiler {
      color: @text;
      background: @text;
    }
    .spoiler:hover {
      background: none;
    }
    code {
      color: @red !important;
      background: fade(@red, 10%) !important;
    }
    hr {
      border-top-color: @surface1 !important;
    }
    .admonition {
      &.info,
      &.hint,
      &.tip {
        color: @blue;
        background: fade(@blue, 25%);
        > .admonition-title::before {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16"><path fill="@{blue}" d="M2 6a6 6 0 1 1 10.174 4.31c-.203.196-.359.4-.453.619l-.762 1.769A.5.5 0 0 1 10.5 13h-5a.5.5 0 0 1-.46-.302l-.761-1.77a2 2 0 0 0-.453-.618A5.98 5.98 0 0 1 2 6m3 8.5a.5.5 0 0 1 .5-.5h5a.5.5 0 0 1 0 1l-.224.447a1 1 0 0 1-.894.553H6.618a1 1 0 0 1-.894-.553L5.5 15a.5.5 0 0 1-.5-.5"/></svg>'
          );
          content: url("data:image/svg+xml,@{svg}");
        }
      }
      &.note,
      &.important {
        color: @green;
        background-color: fade(@green, 25%);
        &,
        .admonition.greentext {
          > .admonition-title::before {
            @svg: escape(
              '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16"><path fill="@{green}" d="M0 2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2zm8.93 4.588-2.29.287-.082.38.45.083c.294.07.352.176.288.469l-.738 3.468c-.194.897.105 1.319.808 1.319.545 0 1.178-.252 1.465-.598l.088-.416c-.2.176-.492.246-.686.246-.275 0-.375-.193-.304-.533zM8 5.5a1 1 0 1 0 0-2 1 1 0 0 0 0 2"/></svg>'
            );
            content: url("data:image/svg+xml,@{svg}");
          }
        }
      }
      &.greentext {
        color: @green;
        background-color: fade(@yellow, 25%);
      }
      &.warning,
      &.caution,
      &.attention {
        color: @yellow;
        background-color: fade(@yellow, 25%);
        > .admonition-title::before {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16"><path fill="@{yellow}" d="M8.982 1.566a1.13 1.13 0 0 0-1.96 0L.165 13.233c-.457.778.091 1.767.98 1.767h13.713c.889 0 1.438-.99.98-1.767zM8 5c.535 0 .954.462.9.995l-.35 3.507a.552.552 0 0 1-1.1 0L7.1 5.995A.905.905 0 0 1 8 5m.002 6a1 1 0 1 1 0 2 1 1 0 0 1 0-2"/></svg>'
          );
          content: url("data:image/svg+xml,@{svg}");
        }
      }
      &.danger,
      &.error {
        color: @red;
        background-color: fade(@red, 25%);
        > .admonition-title::before {
          @svg: escape(
            '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16"><path fill="@{red}" d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8 4a.905.905 0 0 0-.9.995l.35 3.507a.552.552 0 0 0 1.1 0l.35-3.507A.905.905 0 0 0 8 4m.002 6a1 1 0 1 0 0 2 1 1 0 0 0 0-2"/></svg>'
          );
          content: url("data:image/svg+xml,@{svg}");
        }
      }
      .admonition-title::before,
      &.warning > .admonition-title::before,
      &.danger > .admonition-title::before {
        filter: none !important;
      }
    }
    // code
    .highlighttable {
      border-left-color: @surface0 !important;
    }
    .linenodiv > pre > .normal > a:not(:hover),
    .linenodiv > pre > a:not(:hover) {
      color: @overlay2 !important;
    }
    .entry-text pre,
    .markdownx-preview pre,
    code {
      color: @red !important;
    }
    .highlight {
      pre {
        color: @text !important;
      }
      border-color: @surface0 !important;
    }
    /* tables */
    // help tab/page table
    .mtable > tbody > tr:nth-child(even) {
      background-color: @mantle !important;
    }
    // markdown tables
    .ntable th {
      background-color: @surface1 !important;
      border-color: @surface2 !important;
      color: @text;
    }
    .ntable tr:nth-child(even) {
      background-color: @surface0 !important;
    }
