          }
          &[aria-disabled="true"] {
            background-color: @surface1 !important;
            color: @subtext0 !important;
          }
        }
      }
    }

    /* Drive sharing window material icon */
    .SbpzSe .VfPpkd-StrnGf-rymPhb-f7MjDc {
      color: @accent;
    }

