      > img {
        @svg: escape(replace(@raw, "%COLOR%", @subtext0));
        content: url("data:image/svg+xml,@{svg}");
        filter: none;
      }
      &:hover > img {
        @svg: escape(replace(@raw, "%COLOR%", @text));
        content: url("data:image/svg+xml,@{svg}");
        filter: none;
      }

      &:not(:has(> img)) {
        &::before {
          @svg: escape(replace(@raw_before, "%COLOR%", @subtext0));
          content: url("data:image/svg+xml,@{svg}");
          filter: none;
