      @plus: escape(
        '<svg width="17" height="17" shape-rendering="crispEdges" stroke="@{text}" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M5 2.5H2.5v12H5m7-12h2.5v12H12m-7-6h7M8.5 12V5"/></svg>'
